// Extract and store query from webhook
const webhookData = $input.first().json;
const query = webhookData.body?.query || webhookData.query || '';

if (!query || query.trim() === '') {
  throw new Error('No query provided in request');
}

console.log('=== QUERY RECEIVED ===');
console.log(`Query: "${query}"`);

// Classification logic
const adminPatterns = [
  /technical\s+(escalation|issue|problem)/i, /escalate/i, /technical\s+support/i,
  /system\s+(issue|problem|down|failure)/i, /server\s+(down|issue|problem|crash)/i, /website\s+(down|not\s+working)/i,
  /security\s+(concern|issue|breach|problem)/i, /hack/i, /breach/i, /unauthorized/i, /suspicious/i,
  /data\s+(issue|problem|loss|corruption)/i, /database\s+(issue|problem|error)/i, /data\s+loss/i, /backup/i,
  /integration\s+(problem|issue|down|failure)/i, /api\s+(down|issue|problem|failure|integration)/i,
  /urgent/i, /critical/i, /production/i, /affecting\s+(users|customers|production)/i
];

const queryLower = query.toLowerCase();
let classification = 'customer'; // Default

// Check admin patterns first
for (const pattern of adminPatterns) {
  if (pattern.test(queryLower)) {
    classification = 'admin';
    break;
  }
}

const priority = classification === 'admin' ? 'HIGH' : 'NORMAL';
const timestamp = new Date().toISOString();

console.log(`Classification: ${classification}`);
console.log(`Priority: ${priority}`);

// Return query data to pass to next node
return {
  query: query,
  classification: classification,
  priority: priority,
  timestamp: timestamp,
  triggerAPI: true
};

// Get ALL users from API and classification data
const allInputs = $input.all();
const usersData = allInputs.map(input => input.json);

console.log('=== PROCESSING COMPLETE WORKFLOW ===');
console.log(`Collected ${usersData.length} users from API`);

// Get classification data from the Extract node
let queryData = null;
try {
  queryData = $('Extract and Classify Query').first().json;
  console.log(`Retrieved classification data: ${queryData.classification}`);
} catch (error) {
  throw new Error('Could not retrieve classification data from Extract node');
}

if (!queryData || !queryData.query) {
  throw new Error('No query data found from classification node');
}

console.log(`Processing query: "${queryData.query}"`);
console.log(`Classification: ${queryData.classification}`);
console.log(`Priority: ${queryData.priority}`);

// Check role distribution
const roleCount = {};
usersData.forEach(user => {
  const role = user.role || 'unknown';
  roleCount[role] = (roleCount[role] || 0) + 1;
});
console.log('Available roles:', roleCount);

// Filter users by role
let targetUsers = usersData.filter(user => {
  const userRole = (user.role || '').toLowerCase();
  return userRole === queryData.classification.toLowerCase();
});

console.log(`Found ${targetUsers.length} users with role '${queryData.classification}'`);

if (targetUsers.length === 0) {
  throw new Error(`No users found with role '${queryData.classification}'. Available roles: ${Object.keys(roleCount).join(', ')}`);
}

// Limit to 2 users and show details
targetUsers = targetUsers.slice(0, 2);
targetUsers.forEach((user, index) => {
  console.log(`  ${index + 1}. ${user.name} (${user.email}) - Role: ${user.role}`);
});

// Prepare email content
const isAdmin = queryData.classification === 'admin';
const subjectPrefix = isAdmin ? '[ADMIN - URGENT]' : '[CUSTOMER]';
const urgencyText = isAdmin ? 'URGENT ATTENTION REQUIRED' : 'Standard Support Request';

const emailBody = isAdmin ? 
  `ðŸš¨ ADMIN ALERT - IMMEDIATE ACTION REQUIRED ðŸš¨\n\n${urgencyText}\n\nClassification: ${queryData.classification.toUpperCase()}\nPriority: ${queryData.priority}\nTimestamp: ${queryData.timestamp}\n\n--- SUPPORT REQUEST ---\n${queryData.query}\n--- END REQUEST ---\n\nThis is an administrative query requiring immediate technical attention.\nPlease prioritize this request and respond within 1 hour.\n\nâš ï¸ If this is a security issue, follow emergency protocols immediately.\n\n---\nAutomated n8n Email Routing System\nAPI Integration: escuelajs.co/api/v1/users\nGenerated: ${queryData.timestamp}` :
  `Hello Support Team,\n\nNew customer support request received:\n\nClassification: ${queryData.classification.toUpperCase()}\nPriority: ${queryData.priority}\nReceived: ${queryData.timestamp}\n\n--- CUSTOMER REQUEST ---\n${queryData.query}\n--- END REQUEST ---\n\nPlease respond to this customer inquiry within 24 hours.\nEnsure to follow up with the customer directly.\n\nFor urgent matters, please escalate to the admin team immediately.\n\n---\nAutomated n8n Email Routing System\nAPI Integration: escuelajs.co/api/v1/users\nGenerated: ${queryData.timestamp}\n\nBest regards,\nSupport Team`;

console.log('=== PREPARING EMAIL DATA ===');
console.log(`Subject: ${subjectPrefix} ${urgencyText}`);

// Return email data for each user
return targetUsers.map(user => ({
  // API user data
  userId: user.id,
  userName: user.name,
  userEmail: user.email,
  userRole: user.role,

  // Classification data
  originalQuery: queryData.query,
  classification: queryData.classification,
  priority: queryData.priority,
  timestamp: queryData.timestamp,

  // Email content
  subject: `${subjectPrefix} ${urgencyText}`,
  body: emailBody,
  targetEmail: 'trainingtempmail@gmail.com',
  fromEmail: 'noreply@yourcompany.com'
}));

