// Create final response with all correct data
const emailResults = $input.all();
const firstResult = emailResults[0]?.json || {};

console.log('=== CREATING FINAL RESPONSE ===');
console.log(`Processed ${emailResults.length} email sends`);

// Extract all recipient data
const recipients = emailResults.map(result => ({
  apiUserId: result.json.userId,
  apiUserName: result.json.userName,
  originalEmail: result.json.userEmail,
  role: result.json.userRole,
  notificationSentTo: result.json.targetEmail,
  status: 'sent'
}));

const response = {
  success: true,
  message: `Successfully processed query and sent ${emailResults.length} email notification${emailResults.length === 1 ? '' : 's'}`,
  objective: {
    description: "Email routing system with API integration",
    apiIntegration: "âœ… GET request to https://api.escuelajs.co/api/v1/users",
    queryClassification: "âœ… Customer vs Admin classification",
    emailRouting: "âœ… Role-based email routing",
    targetEmail: "âœ… trainingtempmail@gmail.com"
  },
