    adminQuery: "URGENT: Our API integration is down and affecting our production systems. We need immediate technical support to resolve this critical issue."
  },
  workflow: {
    name: "Working Final Email Routing",
    version: "2.0",
    status: "completed",
    nodes: 6
  }
};

console.log(`âœ… COMPLETE! Query: ${response.data.query.text}`);
console.log(`âœ… Classification: ${response.data.query.classification}`);
console.log(`âœ… Priority: ${response.data.query.priority}`);
console.log(`âœ… Emails sent: ${response.data.emailDelivery.emailsSent}`);
console.log('âœ… Recipients:');
recipients.forEach((recipient, index) => {
  console.log(`  ${index + 1}. ${recipient.apiUserName} (${recipient.originalEmail}) - Role: ${recipient.role}`);
});

return response;
