// Extract query from webhook payload
const query = $input.first().json.body.query || $input.first().json.query || '';

if (!query) {
  throw new Error('No query provided in request');
}

// Customer query patterns
const customerPatterns = [
  /product\s+inquiry/i,
  /general\s+support/i,
  /sales\s+question/i,
  /billing\s+inquiry/i,
  /feature\s+request/i,
  /charged\s+(twice|double|again)/i,
  /subscription/i,
  /refund/i,
  /pricing/i,
  /purchase/i,
  /payment/i,
  /invoice/i,
  /account\s+(issue|problem)/i,
  /how\s+to\s+use/i,
  /demo/i,
  /trial/i,
  /plan/i,
  /upgrade/i,
  /downgrade/i,
  /cancel/i
];

// Admin query patterns
const adminPatterns = [
  /technical\s+escalation/i,
  /system\s+issue/i,
  /security\s+concern/i,
  /data\s+issue/i,
  /integration\s+problem/i,
  /urgent/i,
  /critical/i,
  /emergency/i,
  /api\s+(down|error|issue|failure)/i,
  /production\s+systems?/i,
  /server\s+(down|error|crash|failure)/i,
  /database\s+(error|issue|corruption|down)/i,
  /security\s+breach/i,
  /data\s+(loss|corruption)/i,
  /backup\s+(failed|issue|error)/i,
  /infrastructure/i,
  /outage/i,
  /vulnerability/i,
  /hack/i,
  /malware/i,
  /breach/i
];

// Classification function
function classifyQuery(queryText) {
  const lowerQuery = queryText.toLowerCase();

  // Check admin patterns first (higher priority)
  for (const pattern of adminPatterns) {
    if (pattern.test(lowerQuery)) {
      return 'admin';
    }
  }

  // Check customer patterns
  for (const pattern of customerPatterns) {
    if (pattern.test(lowerQuery)) {
      return 'customer';
    }
  }

  // Default to customer for unclassified queries
  return 'customer';
}

const classification = classifyQuery(query);
const timestamp = new Date().toISOString();
const priority = classification === 'admin' ? 'HIGH' : 'NORMAL';

console.log(`Query classified as: ${classification}`);
console.log(`Priority: ${priority}`);
console.log(`Original query: ${query}`);

return {
  query: query,
  classification: classification,
  priority: priority,
  timestamp: timestamp,
  processed: true
};

// Get all users from API response
const usersResponse = $('Fetch Users API').first().json;
const classification = $('Classify Query').first().json.classification;
const queryData = $('Classify Query').first().json;

// Extract users array (handle different response structures)
let users = [];
if (Array.isArray(usersResponse)) {
  users = usersResponse;
} else if (usersResponse.data && Array.isArray(usersResponse.data)) {
  users = usersResponse.data;
} else if (usersResponse.users && Array.isArray(usersResponse.users)) {
  users = usersResponse.users;
} else {
  throw new Error('Could not extract users from API response');
}

console.log(`Total users fetched: ${users.length}`);
console.log(`Looking for users with role: ${classification}`);

// Filter function
function filterUsersByRole(allUsers, targetRole) {
  // Try to filter by existing role field
  let filteredUsers = allUsers.filter(user => {
    const userRole = (user.role || user.type || '').toLowerCase();
    return userRole === targetRole.toLowerCase();
  });

  console.log(`Found ${filteredUsers.length} users with exact role match`);

  // If no exact matches, simulate role assignment for demo
  if (filteredUsers.length === 0) {
    console.log('No exact role matches found, simulating role assignment...');

    if (targetRole === 'admin') {
      // Assign admin role to users with ID divisible by 3
      filteredUsers = allUsers.filter(user => user.id && user.id % 3 === 0).slice(0, 2);
    } else {
      // Assign customer role to other users
      filteredUsers = allUsers.filter(user => user.id && user.id % 3 !== 0).slice(0, 3);
    }

    // Add role property for clarity
    filteredUsers = filteredUsers.map(user => ({
      ...user,
      role: targetRole,
      assigned_role: true // Flag to show this was assigned for demo
    }));
  }

  return filteredUsers;
}

const targetUsers = filterUsersByRole(users, classification);

if (targetUsers.length === 0) {
  throw new Error(`No users available for role: ${classification}`);
}

console.log(`Selected ${targetUsers.length} users for notification`);

// Return user data with query information
return targetUsers.map(user => ({
  // User information
  id: user.id,
  name: user.name || `User ${user.id}`,
  email: user.email,
  role: user.role || classification,
  assigned_role: user.assigned_role || false,

  // Query information
  query: queryData.query,
  classification: queryData.classification,
  priority: queryData.priority,
  timestamp: queryData.timestamp,

  // Email template data
  subject_prefix: classification === 'admin' ? '[ADMIN - URGENT]' : '[CUSTOMER]',
  urgency_text: classification === 'admin' ? 'URGENT ATTENTION REQUIRED' : 'Standard Support Request'
}));

