// Aggregate results from email sending
const allResults = $input.all();
const queryData = $('Classify Query').first().json;

// Count successful email sends
const emailsSent = allResults.length;
const recipients = allResults.map(item => ({
  name: item.json.name,
  email: item.json.email,
  role: item.json.role,
  assigned_role: item.json.assigned_role || false
}));

// Calculate processing time
const startTime = new Date(queryData.timestamp);
const endTime = new Date();
const processingTimeMs = endTime - startTime;

const response = {
  success: true,
  message: "Query processed and emails sent successfully",
  data: {
    query: {
      text: queryData.query,
      classification: queryData.classification,
      priority: queryData.priority,
      timestamp: queryData.timestamp
    },
    processing: {
      emails_sent: emailsSent,
      processing_time_ms: processingTimeMs,
      completed_at: endTime.toISOString()
    },
    recipients: recipients
  },
  workflow: {
    name: "Email Routing Workflow",
    version: "1.0",
    node_count: 8
  }
};

console.log('Workflow completed successfully');
console.log(`Emails sent: ${emailsSent}`);
console.log(`Processing time: ${processingTimeMs}ms`);

return response;
